import { AddCircle, CardGiftcard, EmojiEmotions, Gif } from '@material-ui/icons';
import React, { setState } from 'react';
import '../css/chat.css'
import ChatHeader from './ChatHeader'
import AddCircleIcon from '@material-ui/icons/AddCircle'
import CardGiftcardIcon from '@material-ui/icons/CardGiftcard'
import GifIcon from '@material-ui/icons/Gif'
import EmojiEmotionsIcon from '@material-ui/icons/EmojiEmotions'
import Message from './Message'
import { useSelector } from 'react-redux';
import { selectedChaneId, selectedChanelName } from '../features/appSlice';
import { selectUser } from '../features/userSlice'

const Chat = () => {
    const channelId = useSelector(selectedChaneId);
    const user = useSelector(selectUser);
    const channelName = useSelector(selectedChanelName)
    const [input, setInput] = setState("");

    return (
        <div className="chat">
            <ChatHeader channelName={channelName} />

            <div className="chat__messages">
                <Message />
            </div>

            <div className="chat__input">
                <AddCircleIcon fontSize="large" />
                <form  >
                    <input value={input} onChange={(e) => setInput(e.target.value)} type="text" placeholder={'Message #TESTCHANNEL'} />
                    <button className="chat__inputButton" type='submit'>
                        Send Message
                    </button>
                </form>

                <div className="chat__inputActions">
                    <CardGiftcardIcon fontSize="large" />
                    <GifIcon fontSize="large" />
                    <EmojiEmotionsIcon fontSize="large" />
                </div>
            </div>
        </div>
    );
}

export default Chat;
